$(document).ready(function() {
    $('select').niceSelect();

    $('.toggle_mnu').on('click', function (event) {
	  $(this).toggleClass('on');
	  $(".header-hide").toggle("fade");
	});

 	$(".fancybox").fancybox({
 		closeBtn: 'true',
		afterShow: function () {
   			$('.fancybox-close').attr('id', 'fancybox-close');
		}
	});

	$("#menu").on("click", "a", function (event) {
		event.preventDefault();
		var id = $(this).attr('href'),
			top = $(id).offset().top;
		$('body,html').animate({ scrollTop: top - 100 }, 400);
	});
}); 